import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';

const columns = [
  { id: 'name', label: 'Name', minWidth: 20,align:'ri' },
  { id: 'Monday', label: 'Monday', minWidth: 20, align:'right' },
  {
    id: 'Tuesday',
    label: 'Tuesday',
    minWidth: 30,
    align: 'right',
    format: value => value.toLocaleString(),
  },
  {
    id: 'Wednsday',
    label: 'Wednsday',
    minWidth: 30,
    align: 'right',
    format: value => value.toLocaleString(),
  },
  {
    id: 'Thursday',
    label: 'Thursday',
    minWidth: 30,
    align: 'right',
    format: value => value.toFixed(2),
  },
  {
    id: 'Friday',
    label: 'Friday',
    minWidth: 30,
    align: 'right',
    format: value => value.toFixed(2),
  }
];

function createData(name, Monday, Tuesday, Wednsday,Thursday, Friday) {
  // const density = population / size;
  return { name, Monday, Tuesday,Wednsday, Thursday, Friday };
}

const rows = [
  createData('recruit 1', '1', '1', '1','1', 0.75),
  createData('recruit 2', '0.75', '1', '0', '1',0.25),
  createData('recruit 2', '0.75', '1', '0', '1',0.25),
  createData('recruit 2', '0.75', '1', '0', '1',0.25),
  createData('recruit 2', '0.75', '1', '0', '1',0.25),
  createData('recruit 2', '0.75', '1', '0', '0.5',0.25),
  createData('recruit 2', '0.75', '1', '0', '1',0.25),
  createData('recruit 2', '1', '1', '0.75', '1',0.25)

];

const useStyles = makeStyles({
  root: {
    width: '100%',
  },
  tableWrapper: {
    maxHeight: 440,
    overflow: 'auto',
  },
});

export default function StickyHeadTable() {
  const classes = useStyles();
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = event => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  return (
    <Paper className={classes.root}>
      <div className={classes.tableWrapper}>
        <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              {columns.map(column => (
                <TableCell
                  key={column.id}
                  align={column.align}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map(row => {
              return (
                <TableRow hover role="checkbox" tabIndex={-1} key={row.code}>
                  {columns.map(column => {
                    const value = row[column.id];
                    return (
                      <TableCell key={column.id} align={column.align}>
                        {column.format && typeof value === 'number' ? column.format(value) : value}
                      </TableCell>
                    );
                  })}
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </div>
      <TablePagination
        rowsPerPageOptions={[10, 25, 100]}
        component="div"
        count={rows.length}
        rowsPerPage={rowsPerPage}
        page={page}
        backIconButtonProps={{
          'aria-label': 'previous page',
        }}
        nextIconButtonProps={{
          'aria-label': 'next page',
        }}
        onChangePage={handleChangePage}
        onChangeRowsPerPage={handleChangeRowsPerPage}
      />
    </Paper>
  );
}
